import { Component, OnInit } from "@angular/core";

import { HeroesService, Heroe } from "src/app/service/heroes.service";

@Component({
  selector: "app-heroes",
  templateUrl: "./heroes.component.html",
})
export class HeroesComponent implements OnInit {
  heroes: Heroe[] = [];
  constructor(private _heroesService: HeroesService) {}

  ngOnInit() {
    //useEfect
    this.heroes = this._heroesService.getHeroes();
  }
}
<div class="row row-cols-1 row-cols-md-3 g-4 animated fadeIn">
  <div class="col" *ngFor="let heroe of heroes">
    <div class="card h-100">
      <img src="{{ heroe.img }}" class="card-img-top" alt="..." />
      <div class="card-body d-flex row">
        <h5 class="card-title text-center">{{ heroe.nombre }}</h5>
        <p class="card-text text-center">
          {{ heroe.bio.substring(0, 100) + "..." }}
        </p>
        <button type="button" class="btn btn-outline-success">
          ver mas...
        </button>
        <a ></a>
      </div>
    </div>
  </div>
</div>
