import { Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { HeroesService } from "src/app/service/heroes.service";
import { NavbarComponent } from "../shared/navbar/navbar.component";
@Component({
  selector: "app-buscador",
  templateUrl: "./buscador.component.html",
})
export class BuscadorComponent implements OnInit {
  heroes: any[] = [];
  e: any;
  constructor(
    private activateRouter: ActivatedRoute, //propiedad para setear el parametro de la url
    private heroeService: HeroesService //trae todos los servicios en este caso heroes
  ) {}

  ngOnInit() {
    this.activateRouter.params.subscribe((params) => {
      this.e = params["e"];
      this.heroes = this.heroeService.buscarHeroes(params["e"]);
    });
  }
}
<h1>Buscando: <small>{{e}}</small></h1>
<hr>


<div class="row" *ngIf="heroes.length == 0">
  <div class="">
    <div>

    </div>
  </div>
</div>
<div class="row row-cols-1 row-cols-md-3 g-4 animated fadeIn">
  <div class="col" *ngFor="let heroe of heroes; let i = index">
    <div class="card h-100">
      <img src="{{ heroe.img }}" class="card-img-top" alt="..." />
      <div class="card-body d-flex row">
        <h5 class="card-title text-center">{{ heroe.nombre }}</h5>
        <p class="card-text text-center">
          {{ heroe.bio.substring(0, 100) + "..." }}
        </p>
      </div>
    </div>
  </div>
</div>
