import { Component } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { HeroesService } from "src/app/service/heroes.service";
import { Router } from "@angular/router";
@Component({
  selector: "app-heroe",
  templateUrl: "./heroe.component.html",
  styleUrls: ["./heroe.component.css"],
})
export class HeroeComponent {

  heroe:any={} 

  constructor(private activatedRouter: ActivatedRoute,
    private heroeService: HeroesService,
    private router:Router) {
    this.activatedRouter.params.subscribe((params) => {
   this.heroe = this.heroeService.getHeroe(params['id']) 
    });
  }

  regresar() {
    this.router.navigate(['/heroes'])  }
}
<div class="mt-5 animated fadeIn">
  <h1 class="text-center mt-3">
    {{ heroe.nombre | uppercase }} <small>({{ heroe.aparicion | date:'y' }}</small>
  </h1>

  <div class="d-flex border ">
    <div class="border">
      <img src="{{ heroe.img }}" alt="" />
      <br /><br />
    </div>

    <div class="text-center mt-3">
      <h3 class="">{{ heroe.nombre }}</h3>
      <hr />
      <p class="border ">{{ heroe.bio }}</p>
      <button (click)="regresar()" class="w-25 btn btn-outline-danger">
        Regresar
      </button>
    </div>
  </div>
</div>
